import React, { useState } from 'react';
import './App.css';

// Components for each page
import EventManagement from './components/EventManagement';
import AttendeeManagement from './components/AttendeeManagement';
import TaskTracker from './components/TaskTracker';

function App() {
  const [page, setPage] = useState('events'); // Default page is Event Management
  const [selectedEventId, setSelectedEventId] = useState(null); // Track selected event for TaskTracker

  // Function to switch between pages
  const handlePageChange = (newPage) => {
    setPage(newPage);
  };

  // Handle event selection in EventManagement
  const handleEventSelection = (eventId) => {
    setSelectedEventId(eventId);
    setPage('tasks'); // Automatically navigate to TaskTracker when an event is selected
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Event Management Dashboard</h1>
        <nav>
          <button onClick={() => handlePageChange('events')}>Events</button>
          <button onClick={() => handlePageChange('attendees')}>Attendees</button>
          <button
            onClick={() => handlePageChange('tasks')}
            disabled={!selectedEventId} // Disable if no event is selected
          >
            Tasks
          </button>
        </nav>
      </header>

      <main>
        {page === 'events' && <EventManagement onSelectEvent={handleEventSelection} />}
        {page === 'attendees' && <AttendeeManagement />}
        {page === 'tasks' && selectedEventId && <TaskTracker eventId={selectedEventId} />}
        {page === 'tasks' && !selectedEventId && <p>Please select an event to view tasks.</p>}
      </main>
    </div>
  );
}

export default App;
